import React, { Component } from 'react';
// import CountDownSimple from '../elements/CountDownSimple';
import Link from 'next/link';
import Product from '../elements/products/Product';
import Slider from 'react-slick';
import { carouselStandard } from '../../utilities/carousel-helpers';
import { connect } from 'react-redux';
import { getColletionBySlug } from '../../utilities/product-helper';
import Rating from '../elements/Rating';

class HobbyistHomeSlider extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { collections, collectionSlug } = this.props;
        const products = getColletionBySlug(collections, collectionSlug);
        return (
            <div className="ps-deal-of-day mt-5">
                <div className="ps-container">
                    <div className="ps-section__header">
                        <div className="ps-block--countdown-deal">
                            <div className="ps-block__left">
                                <h3>New Arrivals</h3>
                            </div>
                            {/*<div className="ps-block__right">*/}
                            {/*    <figure>*/}
                            {/*        <figcaption>End in:</figcaption>*/}
                            {/*        <CountDownSimple*/}
                            {/*            timeTillDate="12 31 2020, 6:00 am"*/}
                            {/*            timeFormat="MM DD YYYY, h:mm a"*/}
                            {/*        />*/}
                            {/*    </figure>*/}
                            {/*</div>*/}
                        </div>
                        <Link href="/shop">
                            <a>View all</a>
                        </Link>
                    </div>
                    <div className="ps-section__content">
                        <Slider
                            {...carouselStandard}
                            className="ps-carousel outside">
                            {products.map(product => (
                                <div>
                                    <div className="col-6">
                                        <article className="ps-block--store">
                                            <div
                                                className="ps-block__thumbnail bg--cover"
                                                style={{
                                                    background: `url('/static/img/vendor/store/1.jpg')`,
                                                }}></div>
                                            <div className="ps-block__content">
                                                <div className="ps-block__author">
                                                    <a className="ps-block__user" href="#">
                                                        <img
                                                            src="/static/img/vendor/store/user/5.jpg"
                                                            alt="martfury"
                                                        />
                                                    </a>
                                                    <a className="ps-btn" href="#">
                                                        Shop With Me
                                                    </a>
                                                </div>
                                                <h4>Global Offical</h4>
                                                <Rating/>
                                                <p>325 Orchard, Buenos Aires, Formosa Argentina</p>
                                                <ul className="ps-block__contact">
                                                    <li>
                                                        <i className="icon-envelope"></i>
                                                        <a href="mailto:contact@xhome.com">
                                                            contact@xhome.com
                                                        </a>
                                                    </li>
                                                    <li>
                                                        <i className="icon-telephone"></i> (+093)
                                                        77-637-3300
                                                    </li>
                                                </ul>
                                                <div className="ps-block__inquiry">
                                                    <a href="#">
                                                        <i className="icon-question-circle"></i> inquiry
                                                    </a>
                                                </div>
                                            </div>
                                        </article>
                                    </div>
                                </div>
                            ))}

                        </Slider>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(state => state.collection)(HobbyistHomeSlider);
