import React, { Component } from 'react';
import { Table, Divider, Tag } from 'antd';
import Link from 'next/link';

class TableInvoices extends Component {
    render() {
        /*
            You can change data by API
            example: https://ant.design/components/table/
        */
        const tableData = [
            {
                id: '1',
                invoiceId: '500884010',
                title: 'Marshall Kilburn Portable Wireless Speaker',
                dateCreate: '20-1-2020',
                amount: '42.99',
                status: 'Successful delivery',
            },
            {
                id: '2',
                invoiceId: '593347935',
                title: 'Herschel Leather Duffle Bag In Brown Color',
                dateCreate: '20-1-2020',
                amount: '199.99',
                status: 'Cancel',
            },
            {
                id: '3',
                invoiceId: '593347935',
                title: 'Xbox One Wireless Controller Black Color',
                dateCreate: '20-1-2020',
                amount: '199.99',
                status: 'Cancel',
            },
            {
                id: '4',
                invoiceId: '615397400',
                title: 'Grand Slam Indoor Of Show Jumping Novel',
                dateCreate: '20-1-2020',
                amount: '41.00',
                status: 'Cancel',
            },
        ];
        const tableColumn = [
            {
                title: 'Id',
                dataIndex: 'invoiceId',
                rowKey: 'invoiceId',
                key: 'invoiceId',
                width: '120px',
                render: (text, record) => (
                    <Link href="/account/invoice-detail">
                        {record.invoiceId}
                    </Link>
                ),
            },
            {
                title: 'Title',
                dataIndex: 'title',
                rowKey: 'title',
                key: 'title',
            },
            {
                title: 'Date',
                rowKey: 'dateCreate',
                dataIndex: 'dateCreate',
                key: 'dateCreate',
                width: '120px',
            },
            {
                title: 'Amount',
                rowKey: 'amount',
                dataIndex: 'amount',
                key: 'amount',
                render: (text, record) => (
                    <span className="text-right">${record.amount}</span>
                ),
            },
            {
                title: 'Status',
                key: 'status',
                dataIndex: 'status',
                rowKey: 'status',
                width: '150px',
                render: (text, record) => (
                    <span className="text-right">{record.amount}</span>
                ),
            },
        ];
        return (
            <Table
                columns={tableColumn}
                dataSource={tableData}
                rowKey={record => record.id}
            />
        );
    }
}

export default TableInvoices;
